import React, { useState } from "react";
import Selector from "../components/Selectors/Selector";
import ItemsList from "../components/MarketItems/ItemsList";
import "../styles/Market.css";

const items = [
  {
    id: "127",
    name: "Драконий шлем",
    type: "item",
    itemname: "DRACONICEVOLUTION_DRACONICHELM",
    img: "https://i.mcskill.ru/images/CyLom.png",
    amount: "1",
    stacksize: "1",
    pricerub: "499.00",
    cat: "Draconic Evolution",
  },
  {
    id: "128",
    name: "Драконий Нагрудник",
    type: "item",
    itemname: "DRACONICEVOLUTION_DRACONICHEST",
    img: "https://i.mcskill.ru/images/gCX7e.png",
    amount: "1",
    stacksize: "1",
    pricerub: "499.00",
    cat: "Draconic Evolution",
  },
  {
    id: "129",
    name: "Драконьи паножи",
    type: "item",
    itemname: "DRACONICEVOLUTION_DRACONICLEGGS",
    img: "https://i.mcskill.ru/images/XDhJz.png",
    amount: "1",
    stacksize: "1",
    pricerub: "499.00",
    cat: "Draconic Evolution",
  },
  {
    id: "130",
    name: "Драконьи ботинки",
    type: "item",
    itemname: "DRACONICEVOLUTION_DRACONICBOOTS",
    img: "https://i.mcskill.ru/images/pjrdD.png",
    amount: "1",
    stacksize: "1",
    pricerub: "499.00",
    cat: "Draconic Evolution",
  },
  {
    id: "131",
    name: "Драконий Посох Силы",
    type: "item",
    itemname: "DRACONICEVOLUTION_DRACONICDISTRUCTIONSTAFF",
    img: "https://i.mcskill.ru/images/wfJsh.png",
    amount: "1",
    stacksize: "1",
    pricerub: "1400.00",
    cat: "Draconic Evolution",
  },
  {
    id: "132",
    name: "Драконья кирка",
    type: "item",
    itemname: "DRACONICEVOLUTION_DRACONICPICKAXE",
    img: "https://i.mcskill.ru/images/wZjYj.png",
    amount: "1",
    stacksize: "1",
    pricerub: "800.00",
    cat: "Draconic Evolution",
  },
  {
    id: "133",
    name: "Драконий топор",
    type: "item",
    itemname: "DRACONICEVOLUTION_DRACONICAXE",
    img: "https://i.mcskill.ru/images/NfPKH.png",
    amount: "1",
    stacksize: "1",
    pricerub: "299.00",
    cat: "Draconic Evolution",
  },
];

export default function MarketPage() {
  const servers = [
    { value: "dt", label: "DraconicTech" },
    { value: "tmrpg", label: "TechnoMagicRPG" },
  ];
  const categories = {
    tmrpg: [
      { value: "Thaumcraft", label: "Thaumcraft" },
      { value: "Vanila", label: "Vanila" },
      { value: "IC2", label: "IC2" },
      { value: "Avaritia", label: "Avaritia" },
      { value: "AE2", label: "AE2" },
      { value: "Advanced Solar Panels", label: "Advanced Solar Panels" },
      { value: "Draconic Evolution", label: "Draconic Evolution" },
      { value: "Divine RPG", label: "Divine RPG" },
      { value: "Ender IO", label: "Ender IO" },
      { value: "Extra Utilities", label: "Extra Utilities" },
    ],
    dt: [
      { value: "Draconic Evolution", label: "Draconic Evolution" },
      { value: "IC2", label: "IC2" },
      { value: "Vanila", label: "Vanila" },
      { value: "AE2", label: "AE2" },
      { value: "Advanced Solar Panels", label: "Advanced Solar Panels" },
      { value: "Extra Utilities", label: "Extra Utilities" },
      { value: "Avaritia", label: "Avaritia" },
      { value: "Ender IO", label: "Ender IO" },
      { value: "Other", label: "Разное" },
    ],
  };
  const [selectedServer, setselectedServer] = useState(servers[0]["value"]);
  const [categoriesFind, setCategoriesFind] = useState([]);
  return (
    <div className="market">
      <div className="selector">
        <Selector
          options={servers}
          defaultValue={servers[0]}
          onChange={({ value }) => {
            setCategoriesFind([]);
            setselectedServer(value);
          }}
        />
      </div>
      <input className="find" placeholder="Поиск..."></input>
      <div className="selector">
        <Selector
          value={categoriesFind}
          options={categories[selectedServer]}
          isMulti={true}
          closeMenuOnSelect={false}
          onChange={(value) => {
            setCategoriesFind(value);
            console.log(value);
          }}
        />
      </div>
    </div>
  );
}
