import { useState } from "react";
import UserService from "../../services/UserService";
import Modal from "../modal/Modal";
import SelectRole, { roles } from "../Selectors/SelectRole";
import classes from "./Admin.module.css";

export default function Player({ playerData, user, selectedServer }) {
  const [responseModal, setResponseModal] = useState(false);
  const [name, setName] = useState(playerData.name);
  const [discord, setDiscord] = useState(playerData.discord);
  const [role, setRole] = useState(playerData[selectedServer]["lvl"]);
  const [balance, setBalance] = useState(playerData.balance);
  const [birthday, setBirthday] = useState(playerData.birthday);
  const [modal, setModal] = useState(false);

  const editUser = async (
    playerData,
    selectedServer,
    responseModal,
    setResponseModal
  ) => {
    try {
      await UserService.editUser(
        playerData.id,
        playerData.name,
        playerData.balance,
        playerData.discord,
        playerData.birthday,
        playerData["dt"]["name"],
        playerData["tmrpg"]["name"],
        selectedServer
      );
    } catch (err) {
      let response = err.response.data.message;
      let keys = Object.keys(response);
      if (keys) {
        <Modal visible={responseModal} setVisible={setResponseModal}>
          <h2 style={{ color: "red" }}>
            `${keys[0]} ${response[keys[0]]}`
          </h2>
        </Modal>;
      }
      setResponseModal(true);
    }
    <h1>Test</h1>;
  };

  return (
    <div className={classes.player}>
      <img
        alt=""
        src={`https://skins.mcskill.net/?name=${playerData.name}&mode=1&fx=64&fy=128`}
      ></img>
      <div className={classes.information}>
        <p>Name: {playerData.name}</p>
        <p>DT: {playerData["dt"]["viewName"]}</p>
        <p>TMRPG: {playerData["tmrpg"]["viewName"]}</p>
        <p>Balance: {playerData.balance}</p>
        {user[selectedServer]["lvl"] > playerData[selectedServer]["lvl"] &&
          user[selectedServer]["lvl"] >= 5 && (
            <button
              onClick={() => {
                setName(playerData.name);
                setDiscord(playerData.discord);
                setRole(playerData[selectedServer]["lvl"]);
                setBalance(playerData.balance);
                setBirthday(playerData.birthday);
                setModal(true);
              }}
            >
              Редактировать
            </button>
          )}
      </div>
      <Modal visible={modal} setVisible={setModal}>
        <div className={classes.modalContentMain}>
          <div className={classes.modalContent}>
            <h2>Ник: </h2>
            <input
              maxLength="16"
              value={name}
              onChange={(e) => {
                setName(e.target.value);
              }}
            ></input>
          </div>

          <div className={classes.modalContent}>
            <h2>Дс ID: </h2>
            <input
              maxLength="20"
              value={discord}
              onChange={(e) => {
                setDiscord(e.target.value);
              }}
            ></input>
          </div>

          <div className={classes.modalContent}>
            <h2>Роль: </h2>
            <div className={classes.selector}>
              <SelectRole
                value={role - 1}
                setRole={setRole}
                maxLvl={user["max_role"]["lvl"]}
              />
            </div>
          </div>

          <div className={classes.modalContent}>
            <h2>Баланс: </h2>
            <input
              type="number"
              value={balance}
              onChange={(e) => {
                setBalance(e.target.value);
              }}
            ></input>
          </div>

          <div className={classes.modalContent}>
            <h2>Д/р: </h2>
            <input
              value={birthday}
              onChange={(e) => setBirthday(e.target.value)}
            ></input>
          </div>
        </div>

        <div className={classes.modalContent}>
          <button
            onClick={() => {
              playerData.name = name;
              playerData.discord = discord;
              playerData[selectedServer] = roles[role - 1];
              playerData.balance = balance;
              playerData.birthday = birthday;
              setModal(false);
              editUser(
                playerData,
                selectedServer,
                responseModal,
                setResponseModal
              );
            }}
          >
            Сохранить
          </button>
        </div>
      </Modal>
    </div>
  );
}
