import React, { useState, useEffect, useMemo } from "react";
import Selector from "../components/Selectors/Selector";
import ItemsList from "../components/MarketItems/ItemsList";
import Loader from "../components/Loader/Loader";
import API from "../API/API";
import "../styles/Market.css";

const servers = [
  { value: "dt", label: "DraconicTech" },
  { value: "tmrpg", label: "TechnoMagicRPG" },
];

const categories = {
  tmrpg: [
    { value: "Thaumcraft", label: "Thaumcraft" },
    { value: "Vanila", label: "Vanila" },
    { value: "IC2", label: "IC2" },
    { value: "Avaritia", label: "Avaritia" },
    { value: "AE2", label: "AE2" },
    { value: "Advanced Solar Panels", label: "Advanced Solar Panels" },
    { value: "Draconic Evolution", label: "Draconic Evolution" },
    { value: "Divine RPG", label: "Divine RPG" },
    { value: "Ender IO", label: "Ender IO" },
    { value: "Extra Utilities", label: "Extra Utilities" },
  ],
  dt: [
    { value: "Draconic Evolution", label: "Draconic Evolution" },
    { value: "IC2", label: "IC2" },
    { value: "Vanila", label: "Vanila" },
    { value: "AE2", label: "AE2" },
    { value: "Advanced Solar Panels", label: "Advanced Solar Panels" },
    { value: "Extra Utilities", label: "Extra Utilities" },
    { value: "Avaritia", label: "Avaritia" },
    { value: "Ender IO", label: "Ender IO" },
    { value: "Other", label: "Разное" },
  ],
};

export default function MarketPage() {
  const [items, setItems] = useState([]);
  const [categoriesFilter, setCategoriesFilter] = useState([]);
  const [selectedServer, setselectedServer] = useState(servers[0]["value"]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    document.title = "Магазин";
  }, []);

  const setMarket = async () => {
    const data = await API.getMarketItems();
    if (data) {
      setItems(data);
    }
  };

  const marketItems = useMemo(() => {
    if (categoriesFilter.length > 0) {
      let sortList = [];
      categoriesFilter.map((cat) => {
        return sortList.push(cat["value"]);
      });
      return items.filter((item) => sortList.includes(item["cat"]));
    } else {
      return items;
    }
  }, [categoriesFilter, items]);

  const filteredAndSearchedItems = useMemo(() => {
    return marketItems.filter((item) =>
      item["name"].toLowerCase().includes(search.toLowerCase())
    );
  }, [search, marketItems]);

  useEffect(() => {
    setMarket();
  }, []);

  if (items.length === 0) {
    return <Loader />;
  }

  return (
    <div className="market">
      <div className="selector">
        <Selector
          options={servers}
          defaultValue={servers[0]}
          onChange={({ value }) => {
            setCategoriesFilter([]);
            setselectedServer(value);
          }}
        />
      </div>
      <input
        className="find"
        placeholder="Поиск..."
        onChange={(e) => setSearch(e.target.value)}
      ></input>
      <div className="selector">
        <Selector
          value={categoriesFilter}
          options={categories[selectedServer]}
          isMulti={true}
          closeMenuOnSelect={false}
          onChange={(value) => {
            setCategoriesFilter(value);
          }}
        />
      </div>
      <ItemsList items={filteredAndSearchedItems} />
    </div>
  );
}
