import React, { useContext } from "react";
import { Link, useLocation } from "react-router-dom";
import classes from "./Navigations.module.css";
import Logo from "../Logo.jsx";
import { Context } from "../../index";
import { observer } from "mobx-react-lite";
import {
  ADMIN_ROUTE,
  HISTORY_ROUTE,
  INDEX_ROUTE,
  MARKET_ROUTE,
  STAFF_SALARY_ROUTE,
  viewRoutes,
} from "../../utils/consts";

const Navigation = observer(() => {
  const { user } = useContext(Context);
  const location = useLocation();
  const getColor = (path) => {
    if (path === location.pathname) {
      return classes.selected;
    }
    return classes.unSelected;
  };
  return (
    <div className={classes.navbarDiv}>
      <nav className={classes.navbar}>
        {user.isAuth &&
          viewRoutes[user.user["max_role"]].includes(MARKET_ROUTE) && (
            <Link
              to={MARKET_ROUTE}
              className={`${classes.item} ${getColor(MARKET_ROUTE)}`}
            >
              Магазин
            </Link>
          )}
        {user.isAuth &&
          viewRoutes[user.user["max_role"]].includes(HISTORY_ROUTE) && (
            <Link
              to={HISTORY_ROUTE}
              className={`${classes.item} ${getColor(HISTORY_ROUTE)}`}
            >
              История & Выдача
            </Link>
          )}
        <Link
          to={INDEX_ROUTE}
          className={`${classes.logo} ${getColor(INDEX_ROUTE)}`}
        >
          <Logo />
        </Link>
        <Link
          to={STAFF_SALARY_ROUTE}
          className={`${classes.item} ${getColor({ STAFF_SALARY_ROUTE })}`}
        >
          Зп состава
        </Link>
        {user.isAuth &&
          viewRoutes[user.user["max_role"]].includes(ADMIN_ROUTE) && (
            <Link
              to={ADMIN_ROUTE}
              className={`${classes.item} ${getColor(ADMIN_ROUTE)}`}
            >
              Админ панель
            </Link>
          )}
        {!user.isAuth && (
          <Link
            to={process.env.REACT_APP_DISCORD_LOGIN_URL}
            className={`${classes.item} ${getColor(ADMIN_ROUTE)}`}
          >
            Авторизация
          </Link>
        )}
      </nav>
    </div>
  );
});

export default Navigation;
