import { useState } from "react";
import Modal from "../modal/Modal";
import SelectRole from "../Selectors/SelectRole";
import classes from "./Admin.module.css";

export default function Player({ playerData, user, selectedServer }) {
  const [role, setRole] = useState(playerData[selectedServer]);
  const [modal, setModal] = useState(false);
  return (
    <div className={classes.player}>
      <img
        alt=""
        src={`https://skins.mcskill.net/?name=${playerData.name}&mode=1&fx=64&fy=128`}
      ></img>
      <div className={classes.information}>
        <p>Name: {playerData.name}</p>
        <p>DT: {playerData["dt"]["viewName"]}</p>
        <p>TMRPG: {playerData["tmrpg"]["viewName"]}</p>
        <p>Balance: {playerData.balance}</p>
        {user[selectedServer]["lvl"] > playerData[selectedServer]["lvl"] &&
          user[selectedServer]["lvl"] >= 5 && (
            <button
              onClick={() => {
                setModal(true);
              }}
            >
              Действие
            </button>
          )}
      </div>
      <Modal visible={modal} setVisible={setModal}>
        <div className={classes.modalContent}>
          <h2>Роль: </h2>
          <div className={classes.selector}>
            <SelectRole defaultValue={role["lvl"] - 1} />
          </div>
        </div>
        <div className={classes.modalContent}>
          <h2>Баланс: </h2>
          <input type="number"></input>
        </div>
        <div className={classes.modalContent}>
          <button
            onClick={() => {
              setModal(false);
            }}
          >
            Сохранить
          </button>
        </div>
      </Modal>
    </div>
  );
}
