import React, { useState, useEffect, useMemo } from "react";
import SelectServer from "../components/Selectors/SelectServer";
import SelectCategories from "../components/Selectors/SelectCategories";
import ItemsList from "../components/MarketItems/ItemsList";
import Loader from "../components/Loader/Loader";
import API from "../API/API";
import "../styles/Market.css";

export default function MarketPage() {
  const [items, setItems] = useState({});
  const [categoriesFilter, setCategoriesFilter] = useState([]);
  const [selectedServer, setselectedServer] = useState("dt");
  const [search, setSearch] = useState("");

  useEffect(() => {
    document.title = "Магазин";
  }, []);

  const setMarket = async () => {
    const data = await API.getMarketItems();
    if (data) {
      setItems(data);
    }
  };

  const marketItems = useMemo(() => {
    if (categoriesFilter.length > 0) {
      let sortList = [];
      categoriesFilter.map((cat) => {
        return sortList.push(cat["value"]);
      });
      return items[selectedServer].filter((item) =>
        sortList.includes(item["cat"])
      );
    } else {
      return items[selectedServer];
    }
  }, [categoriesFilter, items, selectedServer]);

  const filteredAndSearchedItems = useMemo(() => {
    console.log(marketItems);
    return marketItems.filter((item) =>
      item["name"].toLowerCase().includes(search.toLowerCase())
    );
  }, [search, marketItems]);

  useEffect(() => {
    setMarket();
  }, []);

  if (Object.keys(items).length === 0) {
    return <Loader />;
  }

  return (
    <div className="market">
      <div className="selector">
        <SelectServer
          onChange={({ value }) => {
            setCategoriesFilter([]);
            setselectedServer(value);
          }}
        />
      </div>
      <input
        className="find"
        placeholder="Поиск..."
        onChange={(e) => setSearch(e.target.value)}
      ></input>
      <div className="selector">
        <SelectCategories
          categoriesFilter={categoriesFilter}
          selectedServer={selectedServer}
          onChange={(value) => {
            setCategoriesFilter(value);
          }}
        />
      </div>
      <ItemsList items={filteredAndSearchedItems} />
    </div>
  );
}
