import React, { useState } from "react";
import classes from "./Items.module.css";
import Modal from "../modal/Modal";

export default function Item({ name, price, img_url, user }) {
  const [amount, setAmount] = useState(0);
  const [modal, setModal] = useState(false);
  return (
    <div className={classes.item}>
      <p
        className={classes.name}
        dangerouslySetInnerHTML={{ __html: name.split("|")[0] }}
      />
      <img alt="" src={img_url}></img>
      {name.includes("|") ? (
        <button onClick={() => setModal(true)}>
          {price + " | " + name.split("|")[1]}
        </button>
      ) : (
        <button onClick={() => setModal(true)}>{price}</button>
      )}
      <Modal visible={modal} setVisible={setModal}>
        <div className={classes.modalContent}>
          <div className={classes.modalInfo}>
            <h3 dangerouslySetInnerHTML={{ __html: name }}></h3>
            <img alt="" src={img_url}></img>
          </div>
          <div className={classes.buy}>
            <input
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              type="number"
              min="0"
              placeholder="Количество..."
            ></input>
            <button
              disabled={user.balance - price * amount < 0}
              onChange={() => {
                console.log("Xyi");
                setModal(false);
              }}
            >
              Купить1
            </button>
            <span>Баланс: {user.balance}</span>
            <span>Останется: {user.balance - price * amount}</span>
          </div>
        </div>
      </Modal>
    </div>
  );
}
