import "chart.js/auto";
import { Bar } from "react-chartjs-2";

const options = {
  plugins: {
    legend: {
      display: false,
    },
  },
  scales: {
    y: {
      title: {
        display: true,
        text: "Value",
      },
      ticks: {
        stepSize: 50,
      },
    },
  },
  maintainAspectRatio: false,
};

export default function ChartStaff({ data }) {
  const dataHorBar = {
    labels: data.labels,
    datasets: [
      {
        indexAxis: "x",
        label: "Итог за неделю",
        data: data.playerData,
        backgroundColor: [
          "#4b0082",
          "#008000",
          "#32cd32",
          "#32cd32",
          "rgba(153, 102, 255, 0.2)",
          "rgba(255, 159, 64, 0.2)",
          "rgba(0, 0, 0, 0.2)",
        ],
        borderColor: [
          "#4b0082",
          "#008000",
          "#32cd32",
          "#32cd32",
          "rgba(153, 102, 255, 1)",
          "rgba(255, 159, 64, 1)",
          "rgba(0, 0, 0, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };
  return (
    <div style={{ height: "600px", width: "100%" }}>
      <Bar height="200px" width="200px" data={dataHorBar} options={options} />
    </div>
  );
}
