import { useState } from "react";
import UserService from "../../services/UserService";
import Modal from "../modal/Modal";
import SelectRole, { roles } from "../Selectors/SelectRole";
import classes from "./Admin.module.css";

export default function PlayerAdd({
  user,
  selectedServer,
  setResponseModal,
  setResponseData,
  modal,
  setModal,
  setStaff,
}) {
  const getUsers = async () => {
    const response = await UserService.fetchUsers();
    response && setStaff(response.data);
  };

  const addUser = async () => {
    setResponseModal(true);
    try {
      await UserService.editUser(
        name,
        discord,
        birthday,
        roles[role - 1].name,
        selectedServer
      );
      setResponseData({
        color: "green",
        content: "Пользователь был успешно добавлен",
      });
      getUsers();
    } catch (err) {
      let response = err.response.data.message;
      let keys = Object.keys(response);
      if (keys) {
        setResponseData({
          color: "red",
          content: `${keys[0]} ${response[keys[0]]}`,
        });
      }
    }
  };

  const [name, setName] = useState("");
  const [discord, setDiscord] = useState("");
  const [role, setRole] = useState(2);
  const [birthday, setBirthday] = useState("");

  return (
    <div>
      <Modal visible={modal} setVisible={setModal}>
        <div className={classes.modalContentMain}>
          <div className={classes.modalContent}>
            <h2>Ник: </h2>
            <input
              maxLength="16"
              value={name}
              onChange={(e) => {
                setName(e.target.value);
              }}
            ></input>
          </div>

          <div className={classes.modalContent}>
            <h2>Дс ID: </h2>
            <input
              maxLength="20"
              value={discord}
              onChange={(e) => {
                setDiscord(e.target.value);
              }}
            ></input>
          </div>

          <div className={classes.modalContent}>
            <h2>Роль: </h2>
            <div className={classes.selector}>
              <SelectRole
                value={role - 1}
                setRole={setRole}
                maxLvl={user["max_role"]["lvl"]}
              />
            </div>
          </div>

          <div className={classes.modalContent}>
            <h2>Д/р: </h2>
            <input
              value={birthday}
              maxLength="32"
              onChange={(e) => setBirthday(e.target.value)}
            ></input>
          </div>
        </div>

        <div className={[classes.player, classes.modalContent].join(" ")}>
          <button
            onClick={() => {
              setModal(false);
              addUser();
            }}
          >
            Добавить
          </button>
        </div>
      </Modal>
    </div>
  );
}
