import React, { useState, useEffect } from "react";
import Modal from "../modal/Modal";
import classes from "./Information.module.css";

export default function InformationSalary({ value, user, selectedServer }) {
  const [amount, setAmount] = useState(value);
  const [modal, setModal] = useState(false);
  // useEffect(() => {
  //   setAmount(value);
  // }, [value]);
  return (
    <div className={classes.information}>
      <div className={classes.coffers}>
        {user[selectedServer]["lvl"] > 4 ? (
          <h2>
            Погашенный итог:{" "}
            <button onClick={() => setModal(true)}>{value}</button>
          </h2>
        ) : (
          <h2>Погашенный итог: {amount}</h2>
        )}
        <p>
          Значение З/П, которое было аннулировано в связи со снятием/Переводом
          модераторов
        </p>
      </div>
      <Modal visible={modal} setVisible={setModal}>
        <div className={classes.salaryModal}>
          <div className={classes.salaryCoffers}>
            <h1>Значение:</h1>
            <input
              value={amount}
              onChange={(e) => {
                if (e.target.value) {
                  setAmount(Math.ceil(e.target.value));
                } else {
                  setAmount("");
                }
              }}
              type="number"
              min="0"
            ></input>
          </div>
          <button
            onClick={() => {
              setModal(false);
            }}
          >
            Установить
          </button>
        </div>
      </Modal>
    </div>
  );
}
