import React, { useEffect, useState } from "react";
import SelectServer from "../Selectors/SelectServer";
import StaffSalary from "../components/tables/StaffSalary";
import CriteriaSalary from "../components/criterias/CriteriaSalary";
import CriteriaViolations from "../components/criterias/CriteriaViolations";
import API from "../../API/API";
import Loader from "../Loader/Loader";
import "../styles/StaffSalary.css";

export default function StaffSalaryPage() {
  const [selectedServer, setselectedServer] = useState("dt");
  const [staffData, setStaffData] = useState({});

  useEffect(() => {
    const setData = async () => {
      const data = await API.getStaffSalaryData();
      if (data) {
        setStaffData(data);
      }
    };
    setData();
    document.title = "Зп состава";
  }, []);

  if (Object.keys(staffData).length === 0) {
    return <Loader />;
  }

  return (
    <div>
      <SelectServer
        onChange={({ value }) => {
          setselectedServer(value);
          console.log(selectedServer);
        }}
      />
      <StaffSalary staffData={staffData} />
      <div className="criteries">
        <CriteriaSalary />
        <CriteriaViolations />
      </div>
    </div>
  );
}
