import React from "react";
import { Link, useLocation } from "react-router-dom";
import classes from "./Navigations.module.css";
import Logo from "../Logo.jsx";
import { useContext } from "react";
import { Context } from "../../index";

export default function Navigation() {
  const { user } = useContext(Context);
  const location = useLocation();
  console.log(user);
  const getColor = (path) => {
    if (path === location.pathname) {
      return classes.selected;
    }
    return classes.unSelected;
  };
  return (
    <div className={classes.navbarDiv}>
      <nav className={classes.navbar}>
        <Link to="/market" className={`${classes.item} ${getColor("/market")}`}>
          Магазин
        </Link>
        <Link
          to="/history"
          className={`${classes.item} ${getColor("/history")}`}
        >
          История & Выдача
        </Link>
        <Link to="/" className={`${classes.logo} ${getColor("/")}`}>
          <Logo />
        </Link>
        <Link
          to="/staffSalary"
          className={`${classes.item} ${getColor("/staffSalary")}`}
        >
          Зп состава
        </Link>
        <Link
          to="/adminPanel"
          className={`${classes.item} ${getColor("/adminPanel")}`}
        >
          Админ панель
        </Link>
      </nav>
    </div>
  );
}
