import React, { useState, useEffect } from "react";
import SelectServer from "../components/Selectors/SelectServer";
import Loader from "../components/Loader/Loader";
import "../styles/Activity.css";
import ActivityService from "../services/ActivityService";

export default function ActivityPage() {
  const [date1, setDate1] = useState("");
  const [date2, setDate2] = useState("");
  const [players, setPlayers] = useState({});
  const [activity, setActivity] = useState({});
  const [activityPlayers, setActivityPlayers] = useState({});
  const [selectedServer, setselectedServer] = useState("dt");

  useEffect(() => {
    const setUsers = async () => {
      const response = await ActivityService.fetchPlayers();
      if (response) {
        setPlayers(response.data);
        console.log(response.data);
        setDate1(response.data["monday"]);
        setDate2(response.data[selectedServer]["wipe"]);
      }
    };
    setUsers();
    document.title = "Игровая активность";
  }, []);

  useEffect(() => {
    Object.keys(players).length > 0 &&
      setActivityPlayers(
        players[selectedServer]["staff"].map((player) => {
          return player.name;
        })
      );
  }, [players, selectedServer, date1, date2]);

  useEffect(() => {
    const getActivity = async () => {
      const response = await ActivityService.fetchActivityPlayers(
        date1,
        date2,
        activityPlayers,
        selectedServer
      );
      response && setActivity(response.data);
    };
    console.log(date1, date2);
    if (date1 && date2) {
      console.log(date1, date2);
      getActivity();
    }
  }, [activityPlayers]);

  if (Object.keys(activity).length === 0) {
    return <Loader />;
  }

  return (
    <div className="activityCheck">
      <div className="selectServer">
        <SelectServer
          onChange={({ value }) => {
            setselectedServer(value);
          }}
        />
      </div>
    </div>
  );
}
