import React, { useState, useEffect } from "react";
import Selector from "../components/Selectors/Selector";
import ItemsList from "../components/MarketItems/ItemsList";
import Loader from "../components/Loader/Loader";
import API from "../API/API";
import "../styles/Market.css";

export default function MarketPage() {
  const servers = [
    { value: "dt", label: "DraconicTech" },
    { value: "tmrpg", label: "TechnoMagicRPG" },
  ];
  const [items, setItems] = useState([]);

  const setMarketItems = async () => {
    const data = await API.getMarketItems();
    if (data) {
      setItems(data);
    }
  };

  useEffect(() => {
    setMarketItems();
  }, []);

  if (items.length === 0) {
    return <Loader />;
  }
  const categories = {
    tmrpg: [
      { value: "Thaumcraft", label: "Thaumcraft" },
      { value: "Vanila", label: "Vanila" },
      { value: "IC2", label: "IC2" },
      { value: "Avaritia", label: "Avaritia" },
      { value: "AE2", label: "AE2" },
      { value: "Advanced Solar Panels", label: "Advanced Solar Panels" },
      { value: "Draconic Evolution", label: "Draconic Evolution" },
      { value: "Divine RPG", label: "Divine RPG" },
      { value: "Ender IO", label: "Ender IO" },
      { value: "Extra Utilities", label: "Extra Utilities" },
    ],
    dt: [
      { value: "Draconic Evolution", label: "Draconic Evolution" },
      { value: "IC2", label: "IC2" },
      { value: "Vanila", label: "Vanila" },
      { value: "AE2", label: "AE2" },
      { value: "Advanced Solar Panels", label: "Advanced Solar Panels" },
      { value: "Extra Utilities", label: "Extra Utilities" },
      { value: "Avaritia", label: "Avaritia" },
      { value: "Ender IO", label: "Ender IO" },
      { value: "Other", label: "Разное" },
    ],
  };
  const [selectedServer, setselectedServer] = useState(servers[0]["value"]);
  const [categoriesFind, setCategoriesFind] = useState([]);
  return (
    <div className="market">
      <div className="selector">
        <Selector
          options={servers}
          defaultValue={servers[0]}
          onChange={({ value }) => {
            setCategoriesFind([]);
            setselectedServer(value);
          }}
        />
      </div>
      <input className="find" placeholder="Поиск..."></input>
      <div className="selector">
        <Selector
          value={categoriesFind}
          options={categories[selectedServer]}
          isMulti={true}
          closeMenuOnSelect={false}
          onChange={(value) => {
            setCategoriesFind(value);
            console.log(value);
          }}
        />
      </div>
      <ItemsList items={items} />
    </div>
  );
}
