import "chart.js/auto";
import { Bar } from "react-chartjs-2";

const colors = {
  backgroundColor: [
    "rgba(255, 26, 104, 0.2)",
    "rgba(54, 162, 235, 0.2)",
    "rgba(255, 206, 86, 0.2)",
    "rgba(75, 192, 192, 0.2)",
    "rgba(153, 102, 255, 0.2)",
    "rgba(255, 159, 64, 0.2)",
    "rgba(0, 0, 0, 0.2)",
  ],
};

const options = {
  plugins: {
    legend: {
      display: false,
    },
  },
  scales: {
    y: {
      title: {
        display: true,
        text: "Value",
      },
      ticks: {
        stepSize: 1,
      },
    },
  },
  maintainAspectRatio: false,
};

export default function ChartActivity({ data }) {
  const getData = () => {
    let playerData = [];
    for (let player in data.playerData) {
      let r = Math.floor(Math.random() * 255);
      let g = Math.floor(Math.random() * 255);
      let b = Math.floor(Math.random() * 255);
      let color = {
        backgroundColor: "rgba(" + r + "," + g + "," + b + "), 0.2",
      };
      playerData.push({ ...data.playerData[player], ...color });
    }
    console.log(playerData);
    return playerData;
  };

  const dataHorBar = {
    labels: data.labels,
    datasets: getData(),
    backgroundColor: colors,
    // datasets: [
    //   {
    //     label: "Онлайн за неделю за неделю",
    //     data: data.playerData,
    //     backgroundColor: [
    //       "rgba(255, 26, 104, 0.2)",
    //       "rgba(54, 162, 235, 0.2)",
    //       "rgba(255, 206, 86, 0.2)",
    //       "rgba(75, 192, 192, 0.2)",
    //       "rgba(153, 102, 255, 0.2)",
    //       "rgba(255, 159, 64, 0.2)",
    //       "rgba(0, 0, 0, 0.2)",
    //     ],
    //     borderColor: [
    //       "rgba(255, 26, 104, 1)",
    //       "rgba(54, 162, 235, 1)",
    //       "rgba(255, 206, 86, 1)",
    //       "rgba(75, 192, 192, 1)",
    //       "rgba(153, 102, 255, 1)",
    //       "rgba(255, 159, 64, 1)",
    //       "rgba(0, 0, 0, 1)",
    //     ],
    //     borderWidth: 1,
    //   },
    // ],
  };
  return (
    <div style={{ height: "600px", width: "100%" }}>
      <Bar height="200px" width="200px" data={dataHorBar} options={options} />
    </div>
  );
}
